mapper.select:
	SELECT num,title,nickname,content,hit,reip,bdate,row_num FROM(
	SELECT num,title,nickname,content,hit,reip,bdate, ROW_NUMBER() OVER(ORDER BY num desc) AS row_num FROM board




	nickname like '%'|| #{searchValue} ||'%'


	title like '%'|| #{searchValue} ||'%'


	content like '%'|| #{searchValue} ||'%'




) numbered_rows WHERE row_num BETWEEN #{begin} AND #{end}
